// src/App.js
import React from 'react';
import './App.css';
import { Route, Routes } from "react-router-dom";
import Navbar from './components/Navbar';
import BookSearchForm from './components/BookSearchForm';
import RegisterForm from './components/RegisterForm';
import RateAndReviewForm from './components/RateAndReviewForm';
import ReadingList from './components/ReadingList';

function App() {
  return (
    <Router>
      <div className="App">
        <header>
          <h1>BOOK FINDER 5000</h1>
        </header>
        <Navbar />
        <Switch>
          <Route path="/" exact component={BookSearchForm} />
          <Route path="/register" component={RegisterForm} />
          <Route path="/rate-review" component={RateAndReviewForm} />
          <Route path="/reading-list" component={ReadingList} />
          {/* Add other routes for different pages/components */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
