import React from 'react';
import { Route, Routes } from "react-router-dom";
import Navbar from './components/Navbar';
import BookSearchForm from './components/BookSearchForm';
import RegisterForm from './components/RegisterForm';
import RateAndReviewForm from './components/RateAndReviewForm';
import ReadingList from './components/ReadingList';

function App() {
  return (
    <div className="App">
      <header style={{ textAlign: 'center' }}>
        <h1 style={{ margin: '0' }}>Library Book Finder</h1>
        <Navbar style={{ justifyContent: 'center' }} /> {/* Center the Navbar */}
      </header>
      <div className="content">
        <Routes>
          <Route path="/" element={<BookSearchForm />} />
          <Route path="/register" element={<RegisterForm />} />
          <Route path="/rate-review" element={<RateAndReviewForm />} />
          <Route path="/reading-list" element={<ReadingList />} />
        </Routes>
      </div>
    </div>
  );
}

export default App;
